import { useQuery } from '@apollo/react-hooks';

import { GET_BOOK } from '../queries/queries';

const BookDetails = (props) => {
  const [ book ] = useQuery(GET_BOOK, {
    options: (props) => {
      return {
        variables: {
          id: props.bookId
        }
      }
    }
  });

  console.log(props)

  return(
    <div>
      <div id="book-details">
        <p>Output book details here</p>
      </div>
    </div>
  );
}

export default BookDetails;
