import React, { useState } from 'react'
import { useQuery, useMutation } from '@apollo/react-hooks';

import { ADD_BOOK, ADD_BOOK_MUTATION } from '../queries/queries';

const AddBook = () => {
  const [state , setState] = useState({
    name: '',
    genre: '',
    authorId: ''
  });

  const { loading, error, data } = useQuery(ADD_BOOK);
  const { dataM } = useMutation(ADD_BOOK_MUTATION, {
    variables: {
      name: state.name,
      genre: state.genre,
      authorId: state.authorId
    }
  });

  if (loading) return <div>Loading</div>;
  if (error) return <div>Error</div>;

  const printAuthors = data.authors.map(author => {
    return (
      <option key={author.id} value={author.id}>{author.name}</option>
    )
  })

  const submitForm = e => {
    e.preventDefault();
    console.log(state);
    dataM();
  }

  return(
    <form id="add-book" onSubmit={submitForm}>

      <div className="field">
        <label>Book Name</label>
        <input type="text" onChange={(e)=> setState({...state, name: e.target.value})} />
      </div>

      <div className="field">
        <label>Genre</label>
        <input type="text" onChange={(e)=> setState({...state, genre: e.target.value})} />
      </div>

      <div className="field">
        <label>Author</label>
        <select onChange={(e)=> setState({...state, authorId: e.target.value})}>
          <option>Select author</option>
          {printAuthors}
        </select>
      </div>

      <button>+</button>

    </form>
  );
}

export default AddBook;
