import gql from 'graphql-tag';

const ADD_BOOK = gql`
  {
    authors {
      name
      id
    }
  }
`;

const BOOK_LIST = gql`
  {
    books {
      name
      id
    }
  }
`;

const ADD_BOOK_MUTATION = gql`
  mutation($name: String!, $genre: String!, $authorId: ID!) {
    addBook(name: $name, genre: $genre, authorId: $authorId) {
      name
      id
    }
  }
`;

export { ADD_BOOK, BOOK_LIST, ADD_BOOK_MUTATION };
